                                    style={{
                                        padding: "0.5rem 0.75rem",
                                        color: "#374151",
                                        lineHeight: "1.25",
                                        width: "100%",
                                        borderRadius: "0.25rem",
                                        borderWidth: "1px",
                                        appearance: "none",
                                        boxShadow: "0 1px 3px 0 rgba(0, 0, 0, 0, 1), 0 1px 2px 0 rgba (0, 0, 0, 0, 06)",
