// TODO: answer here
import { useState } from 'react';
import Navbar from '../components/Navbar';
import { useNavigate } from "react-router-dom";

const AddStudent = () => {
    // TODO: answer here
    const navigate = useNavigate();
    const [fullname, setFullname] = useState('')
    const [profilePicture, setProfilePicture] = useState('')
    const [address, setAddress] = useState('')
    const [phoneNumber, setPhoneNumber] = useState('')
    const [birthDate, setBirthDate] = useState('')
    const [gender, setGender] = useState('')
    const [faculty, setFaculty] = useState('')
    const [programStudy, setProgramStudy] = useState('')

    const fullnameChange = (e) => setFullname(e.target.value)
    const profilePictureChange = (e) => setProfilePicture(e.target.value)
    const addressChange = (e) => setAddress(e.target.value)
    const phoneNumberChange = (e) => setPhoneNumber(e.target.value)
    const birthDateChange = (e) => setBirthDate(e.target.value)
    const genderChange = (e) => setGender(e.target.value)
    const programStudyChange = (e) => {
        const value = e.target.value
        setProgramStudy(value)

        if (['Ekonomi', 'Manajemen', 'Akuntansi'].includes(value)) {
            setFaculty('Fakultas Ekonomi')
        } else if (['Administrasi Publik', 'Administrasi Bisnis', 'Hubungan Internasional'].includes(value)) {
            setFaculty('Fakultas Ilmu Sosial dan Politik')
        } else if (['Teknik Sipil', 'Arsitektur'].includes(value)) {
            setFaculty('Fakultas Teknik')
        } else if (['Matematika', 'Fisika', 'Informatika'].includes(value)) {
            setFaculty('Fakultas Teknologi Informasi dan Sains')
        }
    }

    const handleSubmit = async () => {
        let dataForSubmit = {
            fullname,
            profilePicture,
            address,
            phoneNumber,
            birthDate,
            gender,
            faculty,
            programStudy,
        }

        await fetch('http://localhost:3001/student', {
            method: "POST",
            headers: {
                "Content-Type": "application/json",
            },
            body: JSON.stringify(dataForSubmit),
        });

        navigate("/student");
    }

    return (
        <>
            <Navbar menu='add' />
            <Container>
                <Row>
                    <Col md={12}>
                        <h4>Add Student</h4>
                        <Card bg='light' key='light' text='dark' className='mt-4'>
                            <Card.Body>
                                <Row>
                                    <Col md={12}>
                                        <Form.Group className="mb-3" controlId="addStudentForm.fullname">
                                            <Form.Label>Fullname</Form.Label>
                                            <Form.Control type="text" placeholder="John Doe" onChange={fullnameChange} data-testid="name" />
                                        </Form.Group>
                                    </Col>
                                    <Col md={12}>
                                        <Form.Group className="mb-3" controlId="addStudentForm.profilePicture">
                                            <Form.Label>Profile Picture</Form.Label>
                                            <Form.Control type="text" placeholder="https://placehold.com/image.jpg" onChange={profilePictureChange} data-testid="profilePicture" />
                                        </Form.Group>
                                    </Col>
                                    <Col md={12}>
                                        <Form.Group className="mb-3" controlId="addStudentForm.address">
                                            <Form.Label>Address</Form.Label>
                                            <Form.Control type="text" placeholder="City, Country" onChange={addressChange} data-testid="address" />
                                        </Form.Group>
                                    </Col>
                                    <Col md={12}>
                                        <Form.Group className="mb-3" controlId="addStudentForm.phoneNumber">
                                            <Form.Label>Phone Number</Form.Label>
                                            <Form.Control type="text" placeholder="+628XXXXXXX" onChange={phoneNumberChange} data-testid="phoneNumber" />
                                        </Form.Group>
                                    </Col>
                                    <Col md={6}>
                                        <Form.Group className="mb-3" controlId="addStudentForm.birthDate">
                                            <Form.Label>Birth Date</Form.Label>
                                            <Form.Control type="date" onChange={birthDateChange} data-testid="date" />
                                        </Form.Group>
                                    </Col>
                                    <Col md={6}>
                                        <Form.Group className="mb-3" controlId="addStudentForm.gender">
                                            <Form.Label>Gender</Form.Label>
                                            <Form.Select onChange={genderChange} data-testid="gender">
                                                <option>Choose your Gender</option>
                                                <option>Male</option>
                                                <option>Female</option>
                                            </Form.Select>
                                        </Form.Group>
                                    </Col>
                                    <Col md={12}>
                                        <Form.Group className="mb-3" controlId="addStudentForm.programStudy">
                                            <Form.Label>Program Study</Form.Label>
                                            <Form.Select onChange={programStudyChange} data-testid="prody">
                                                <option>Choose your Program Study</option>
                                                <optgroup label="Fakultas Ekonomi">
                                                    <option>Ekonomi</option>
                                                    <option>Manajemen</option>
                                                    <option>Akuntansi</option>
                                                </optgroup>
                                                <optgroup label="Fakultas Ilmu Sosial dan Politik">
                                                    <option>Administrasi Publik</option>
                                                    <option>Administrasi Bisnis</option>
                                                    <option>Hubungan Internasional</option>
                                                </optgroup>
                                                <optgroup label="Fakultas Teknik">
                                                    <option>Teknik Sipil</option>
                                                    <option>Arsitektur</option>
                                                </optgroup>
                                                <optgroup label="Fakultas Teknologi Informasi dan Sains">
                                                    <option>Matematika</option>
                                                    <option>Fisika</option>
                                                    <option>Informatika</option>
                                                </optgroup>
                                            </Form.Select>
                                        </Form.Group>
                                        <Button variant="primary" onClick={handleSubmit} data-testid="add-btn">Add Student</Button>
                                    </Col>
                                </Row>
                            </Card.Body>
                        </Card>
                    </Col>
                </Row>
            </Container>
        </>
    );
};

export default AddStudent;
