// TODO: answer here
import Container from 'react-bootstrap/Container';
import Nav from 'react-bootstrap/Nav';
import Navbar from 'react-bootstrap/Navbar';
import {
    Link,
} from "react-router-dom";

const NavBar = ({ menu = '' }) => {
    return (
        // TODO: answer here
        <Navbar bg="light" expand="lg" className='mb-2'>
            <Container fluid>
                <Link to="/" data-testid="home-page" className='navbar-brand'>Student Portal</Link>
                <Navbar.Toggle aria-controls="basic-navbar-nav" />
                <Navbar.Collapse id="basic-navbar-nav" className="justify-content-end">
                    <Nav>
                        <Link to="/student" data-testid="student-page" className={`nav-link${menu === 'student' ? ' active' : ''}`}>All Student</Link>
                        <Link to="/add" data-testid="add-page" className={`nav-link${menu === 'add' ? ' active' : ''}`}>Add Student</Link>
                    </Nav>
                </Navbar.Collapse>
            </Container>
        </Navbar>
    );
};

export default NavBar;
