// TODO: answer here
import Container from 'react-bootstrap/Container';
import Row from 'react-bootstrap/Row';
import Col from 'react-bootstrap/Col';
import Form from 'react-bootstrap/Form';
import Button from 'react-bootstrap/Button';
import Card from 'react-bootstrap/Card';
import { useParams } from "react-router-dom";
import { useState } from 'react';
import Navbar from '../components/Navbar';
import { useEffect } from 'react';
import { useNavigate } from "react-router-dom";

const EditStudent = () => {
    // TODO: answer here
    const navigate = useNavigate();
    let { id: studentId } = useParams()

    // TODO: answer here
    const [loading, setLoading] = useState(true)
    const [fullname, setFullname] = useState('')
    const [profilePicture, setProfilePicture] = useState('')
    const [address, setAddress] = useState('')
    const [phoneNumber, setPhoneNumber] = useState('')
    const [birthDate, setBirthDate] = useState('')
    const [gender, setGender] = useState('')
    const [faculty, setFaculty] = useState('')
    const [programStudy, setProgramStudy] = useState('')

    const fullnameChange = (e) => setFullname(e.target.value)
    const addressChange = (e) => setAddress(e.target.value)
    const phoneNumberChange = (e) => setPhoneNumber(e.target.value)
    const birthDateChange = (e) => setBirthDate(e.target.value)
    const genderChange = (e) => setGender(e.target.value)
    const programStudyChange = (e) => {
        const value = e.target.value
        setProgramStudy(value)

        if (['Ekonomi', 'Manajemen', 'Akuntansi'].includes(value)) {
            setFaculty('Fakultas Ekonomi')
        } else if (['Administrasi Publik', 'Administrasi Bisnis', 'Hubungan Internasional'].includes(value)) {
            setFaculty('Fakultas Ilmu Sosial dan Politik')
        } else if (['Teknik Sipil', 'Arsitektur'].includes(value)) {
            setFaculty('Fakultas Teknik')
        } else if (['Matematika', 'Fisika', 'Informatika'].includes(value)) {
            setFaculty('Fakultas Teknologi Informasi dan Sains')
        }
    }

    const fetchStudent = async (id) => {
        fetch(`http://localhost:3001/student/${id}`)
        .then(response => response.json())
        .then(response => {
            setFullname(response.fullname || '')
            setProfilePicture(response.profilePicture || '')
            setAddress(response.address || '')
            setPhoneNumber(response.phoneNumber || '')
            setBirthDate(response.birthDate || '')
            setGender(response.gender || '')
            setFaculty(response.faculty || '')
            setProgramStudy(response.programStudy || '')
            setLoading(false)
        })
    }

    const handleSubmit = async () => {
        let dataForSubmit = {
            fullname,
            profilePicture,
            address,
            phoneNumber,
            birthDate,
            gender,
            faculty,
            programStudy,
        }

        fetch(`http://localhost:3001/student/${studentId}`, {
            method: "PUT",
            headers: {
                "Content-Type": "application/json",
            },
            body: JSON.stringify(dataForSubmit),
        })
        .then(response => response.json())
        .then(response => {
            navigate("/student");
        });
    }

    useEffect(() => {
        fetchStudent(studentId)
    }, [studentId])

    return (
        <>
            <Navbar menu='student' />
            <Container>
                <Row hidden={!loading}>
                    <Col md={12}>
                        <center><p><b>Loading ...</b></p></center>
                    </Col>
                </Row>
                <Row hidden={loading}>
                    <Col md={2}>
                        <img src={profilePicture} width={100} style={{ width: '100%' }} className='mt-4' alt='Student Profile' />
                    </Col>
                    <Col md={10}>
                        <Card bg='light' key='light' text='dark' className='mt-4'>
                            <Card.Body>
                                <Row>
                                    <Col md={12}>
                                        <Form.Group className="mb-3" controlId="addStudentForm.fullname">
                                            <Form.Label>Fullname</Form.Label>
                                            <Form.Control type="text" placeholder="John Doe" value={fullname} onChange={fullnameChange} data-testid="name" />
                                        </Form.Group>
                                    </Col>
                                    <Col md={12}>
                                        <Form.Group className="mb-3" controlId="addStudentForm.address">
                                            <Form.Label>Address</Form.Label>
                                            <Form.Control type="text" placeholder="City, Country" value={address} onChange={addressChange} data-testid="address" />
                                        </Form.Group>
                                        </Col>
                                    <Col md={12}>
                                        <Form.Group className="mb-3" controlId="addStudentForm.phoneNumber">
                                            <Form.Label>Phone Number</Form.Label>
                                            <Form.Control type="text" placeholder="+628XXXXXXX" value={phoneNumber} onChange={phoneNumberChange} data-testid="phoneNumber" />
                                        </Form.Group>
                                        </Col>
                                    <Col md={6}>
                                        <Form.Group className="mb-3" controlId="addStudentForm.birthDate">
                                            <Form.Label>Birth Date</Form.Label>
                                            <Form.Control type="date" value={birthDate} onChange={birthDateChange} data-testid="date" />
                                        </Form.Group>
                                        </Col>
                                    <Col md={6}>
                                        <Form.Group className="mb-3" controlId="addStudentForm.gender">
                                            <Form.Label>Gender</Form.Label>
                                            <Form.Select value={gender} onChange={genderChange} data-testid="gender">
                                                <option>Choose your Gender</option>
                                                <option>Male</option>
                                                <option>Female</option>
                                            </Form.Select>
                                        </Form.Group>
                                        </Col>
                                    <Col md={12}>
                                        <Form.Group className="mb-3" controlId="addStudentForm.programStudy">
                                            <Form.Label>Program Study</Form.Label>
                                            <Form.Select value={programStudy} onChange={programStudyChange} data-testid="prody">
                                                <option>Choose your Program Study</option>
                                                <optgroup label="Fakultas Ekonomi">
                                                    <option>Ekonomi</option>
                                                    <option>Manajemen</option>
                                                    <option>Akuntansi</option>
                                                </optgroup>
                                                <optgroup label="Fakultas Ilmu Sosial dan Politik">
                                                    <option>Administrasi Publik</option>
                                                    <option>Administrasi Bisnis</option>
                                                    <option>Hubungan Internasional</option>
                                                </optgroup>
                                                <optgroup label="Fakultas Teknik">
                                                    <option>Teknik Sipil</option>
                                                    <option>Arsitektur</option>
                                                </optgroup>
                                                <optgroup label="Fakultas Teknologi Informasi dan Sains">
                                                    <option>Matematika</option>
                                                    <option>Fisika</option>
                                                    <option>Informatika</option>
                                                </optgroup>
                                            </Form.Select>
                                        </Form.Group>
                                        <Button variant="primary" onClick={handleSubmit} data-testid="edit-btn">Edit Student</Button>
                                    </Col>
                                </Row>
                            </Card.Body>
                        </Card>
                    </Col>
                </Row>
            </Container>
        </>
    );
};

export default EditStudent;
